import OptionButton from '@components/ui/OptionButton';
import { CustomProductOptionFragment } from '@graphql/generated/graphql';
import { useCustomiserStore } from '@store/customiser';
import { motion } from 'framer-motion';
import NavButtons from '../NavButtons';
import styles from './NavFitting.module.scss';
import { FocusCameraOnPart } from '@components/layout/Customiser/Customiser';
import CapsuleButton from '@components/ui/Button/CapsuleNavButton';
import { useState } from 'react';
import ContainedButton from '@components/ui/Button/ContainedButton';
import ContainedNavButton from '@components/ui/Button/ContainedNavButton';

export interface FittingOptionProps {
  option: CustomProductOptionFragment;
  focusCameraOnPart?: FocusCameraOnPart;
}

const FittingOption = ({ option, focusCameraOnPart }: FittingOptionProps) => {
  const selectedModels = useCustomiserStore((state) => state.selectedModels);
  const setSelectedModel = useCustomiserStore((state) => state.setSelectedModel);

  if (!option) {
    return null;
  }

  const getSelected = selectedModels.find((m) => m.optionId === option.id)?.model;

  return (
    <>
      <motion.div
        className={styles.fittingOption}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <div className={styles.fittingButtons}>
          {option.models?.map((m) => (
            <ContainedNavButton
              key={m?.id}
              isSelected={getSelected?.id === m?.model?.data?.id}
              classNames={styles.fittingButton}
              onClick={() => {
                setSelectedModel(option.id, m?.model?.data);
                focusCameraOnPart && focusCameraOnPart(option.name);
              }}
            >
              {m?.model?.data?.attributes?.name}
            </ContainedNavButton>
          ))}
        </div>
      </motion.div>
    </>
  );
};

interface NavFittingProps {
  focusCameraOnPart?: FocusCameraOnPart;
}
const NavFitting = ({ focusCameraOnPart }: NavFittingProps) => {
  const fittingOptions = useCustomiserStore(
    (state) => state.customProduct?.attributes?.options,
  ) as Array<CustomProductOptionFragment>;

  const fittingOptionsTitles = fittingOptions?.map((o) => o.name);

  const [navStep, setNavStep] = useState(0);

  return (
    <div className={styles.root}>
      <div className={styles.navButtonContainer}>
        {fittingOptionsTitles.map((title, i) => {
          return (
            <CapsuleButton
              isFinished={navStep > i}
              isSelected={navStep === i}
              onClick={() => setNavStep(i)}
              key={i}
            >
              {title}a
            </CapsuleButton>
          );
        })}
      </div>
      <div>
        <b>Select {fittingOptionsTitles[navStep]}:</b>
      </div>
      <br />
      {
        <FittingOption
          focusCameraOnPart={focusCameraOnPart}
          key={fittingOptions[navStep]?.id}
          option={fittingOptions[navStep]}
        />
      }
      <NavButtons />
    </div>
  );
};

export default NavFitting;
