import FormInput from '@components/ui/FormInput';
import styles from './NavText.module.scss';
import { useCustomiserStore, type TextCustomiser } from '@store/customiser';
import { LegacyRef, forwardRef, useEffect, useState } from 'react';
import cn from 'classnames';

import inputStyles from './../../ui/FormInput/FormInput.module.scss';

interface NavTextInputProps {
  editText?: TextCustomiser;
}

const NavTextInput = forwardRef(({ editText }: NavTextInputProps, ref) => {
  const [text, setText] = useState<string | undefined>(editText?.text);
  const { updateText, addText } = useCustomiserStore((state) => state);

  const setTextOnChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setText(e.target.value);
  };

  const rootClassName = cn(inputStyles.root, styles.textInput);

  useEffect(() => {
    if (editText?.key) {
      updateText(editText.key, {
        text,
        // font: font.url,
        edit: true,
      });
    } else {
      if (text) {
        addText({ text });
      }
    }
  }, [text]);

  useEffect(() => {
    console.log('ref', ref);
  }, [ref]);

  return (
    <div className={styles.textInputContainer}>
      {/* <FormInput
        ref={inputRef}
        className={styles.textInput}
        placeholder='Type text here...'
        value={editText?.text}
        onChange={setTextOnChange}
      /> */}
      <div className={rootClassName} style={!editText?.text ? { visibility: 'hidden' } : {}}>
        <input
          className={inputStyles.input}
          onChange={setTextOnChange}
          ref={ref as LegacyRef<HTMLInputElement> | undefined}
          placeholder='Type text here...'
          value={editText?.text}
        />
        {/* <label className={inputStyles.label}>Type text here</label> */}
      </div>
    </div>
  );
});

NavTextInput.displayName = 'NavTextInput';

export default NavTextInput;
