import { Decal } from '@react-three/drei';
import { TextCustomiser } from '@store/customiser';
import { Texture, Vector2, Vector3 } from 'three';
import Lights from '../Lights';
import { Canvas } from '@react-three/fiber';
import styles from './NavTextScene.module.scss';
import { useEffect, useRef, useState } from 'react';
import { createPortal } from 'react-dom';
import { usePortalRef } from '@hooks';

interface NavTextPreviewSceneInterface {
  text: TextCustomiser;
}

const NavTextPreviewScene = ({ text }: NavTextPreviewSceneInterface) => {
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const [shouldShow, setShouldShow] = useState<boolean>(false);
  const [zoom, setZoom] = useState<number>(1);
  const [dataUrl, setDataUrl] = useState<string>('');

  const portalRef = usePortalRef('popupContainer');

  const scale = 10;
  const scaleModifier = new Vector3(3.2 * scale, 0.4 * scale, 4);

  const isLuxury: boolean = text.selectedName?.id === '2' ? true : false;
  const hasPuff: boolean = text.puffPrice ? true : false;
  const hasCrystals: boolean = text.crystalPrice ? true : false;

  const shouldNormalMap =
    text.normalMap instanceof Texture && text.selectedName?.attributes?.name === 'Luxury';
  const normalMap = shouldNormalMap ? text.normalMap : null;

  let normalScale = 1;
  if (hasCrystals) {
    normalScale = 4;
  } else if (isLuxury && !hasPuff) {
    normalScale = 0.5;
  } else if (isLuxury && hasPuff) {
    normalScale = 3;
  }

  if (!portalRef) return null;

  function addZoom() {
    setZoom((cur) => {
      if (cur >= 10) return cur;
      return (cur += 0.1);
    });
  }
  function subtractZoom() {
    setZoom((cur) => {
      if (cur <= 0.1) return cur;
      return (cur -= 0.1);
    });
  }

  return (
    <>
      {/* <Canvas
        ref={(el) => {
          canvasRef.current = el;
        }}
        gl={{
          preserveDrawingBuffer: true,
        }}
      >
        <Lights />
        <mesh>
          <planeBufferGeometry args={[20, 5]} />
          <meshNormalMaterial visible={false} />
          <Decal position={[0, 0, 0]} rotation={[0, 0, 0]} scale={scaleModifier}>
            <meshStandardMaterial
              map={text.preview instanceof Texture ? text.preview : null}
              transparent
              roughness={isLuxury && !hasCrystals ? 0.75 : 0}
              depthTest
              depthWrite={false}
              normalMap={normalMap}
              normalScale={new Vector2(normalScale, normalScale)}
            />
          </Decal>
        </mesh>
      </Canvas> */}
      <div
        className={styles.canvasClickableCover}
        onClick={() => {
          setShouldShow(true);
          setDataUrl(canvasRef.current?.toDataURL() || '');
        }}
      >
        <span className={styles.canvasClickableCover__imgContainer}>
          <img height={32} width={32} src='/icons/click-icon.png' />
        </span>
      </div>
      {shouldShow &&
        canvasRef &&
        dataUrl &&
        createPortal(
          <>
            <div className={styles.navTextSceneFullscreen}>
              <span
                onClick={() => setShouldShow(false)}
                className={styles.navTextSceneFullscreen__closeBtn}
              >
                x
              </span>
              <span className={styles.navTextSceneFullscreen__imgContainer}>
                <img style={{ zoom }} src={dataUrl} alt='Text Preview' />
              </span>
              <span className={styles.navTextSceneFullscreen__zoomContainer}>
                <img
                  onClick={addZoom}
                  height='32'
                  width='32'
                  src='/icons/zoom-in-icon.png'
                  alt='zoom in'
                />
                <img
                  onClick={subtractZoom}
                  height='32'
                  width='32'
                  src='/icons/zoom-out-icon.png'
                  alt='zoom out'
                />
              </span>
            </div>
          </>,
          portalRef,
        )}
    </>
  );
};

export default NavTextPreviewScene;
