import {
  InputMaybe,
  MaterialColourGroupEntity,
  MaterialEntity,
  MaterialFragment,
  MaterialGroupEntity,
  MaterialTypeEntity,
  Maybe,
  useGetMaterialsQuery,
} from '@graphql/generated/graphql';
import { graphQLClient } from '@graphql/graphql-client';

import cn from 'classnames';
import { useEffect, useState } from 'react';

import styles from './MaterialGroup.module.scss';
import { createStore, Provider, useMaterialGroupStore } from './MaterialGroupState';
import { MaterialType } from './MaterialType';
import SelectedMaterialInfo from './SelectedMaterialInfo';
import { Falsey } from 'lodash';
import { usePortalRef } from '@hooks';
import { createPortal } from 'react-dom';

export interface MaterialGroupProps {
  className?: string;
  materialGroup?: Maybe<MaterialGroupEntity>;
  materialType?: Maybe<MaterialTypeEntity>;
  colourGroup?: Maybe<MaterialColourGroupEntity>;
  onMaterialSelected: (material: MaterialFragment) => void;
  hideColorGroups?: boolean;
  selectedMaterialId?: Falsey | string;
}

const MaterialGroupLoader = ({
  className,
  materialGroup,
  materialType,
  colourGroup,
  onMaterialSelected,
  hideColorGroups,
  selectedMaterialId,
}: MaterialGroupProps) => {
  const colourGroups = useMaterialGroupStore((state) => state.colourGroups);
  const selectedColourGroup = useMaterialGroupStore((state) => state.selectedColourGroup);
  const setMaterials = useMaterialGroupStore((state) => state.setMaterials);
  const setColourGroup = useMaterialGroupStore((state) => state.setColourGroup);
  const rootClassName = cn(styles.root, className);

  const popupPortalRef = usePortalRef('popupContainer');

  const typeIds = materialGroup?.attributes?.materialTypes
    ? (materialGroup.attributes.materialTypes.data.map((t) => t.id) as InputMaybe<string>[])
    : [];

  const [showPopup, setShowPopup] = useState<boolean>(false);
  const [materialZoomImg, setMaterialZoomImg] = useState<string>('');

  const [isLongPress, setIsLongPress] = useState(false);
  const handleMaterialTouchStart = () => {
    // const mapImage = material?.attributes?.images?.find((i) => i?.mapType === 'map');
    // const largeMapImage = mapImage?.image?.data?.attributes?.formats?.large?.url ?? '';
    const largeMapImage =
      'https://boxxer-api-dev.nyc3.cdn.digitaloceanspaces.com/large_reversable_gold_and_black_Base_Color_76d4d72236.jpg';
    setIsLongPress(false);
    // setTimeout(() => {
    //   setMaterialZoomImg(largeMapImage);
    //   setIsLongPress(true);
    // }, 300); // Adjust the 300ms delay as needed
  };
  const handleMaterialTouchEnd = () => setIsLongPress(false);

  const popupCloseHandler = () => {
    setShowPopup(false);
  };

  const onMaterialSelectedHandler = (material: MaterialFragment) => {
    const mapImage = material?.attributes?.images?.find((i) => i?.mapType === 'map');
    const largeMapImage = mapImage?.image?.data?.attributes?.formats?.large?.url ?? '';

    onMaterialSelected(material);
    // setMaterialZoomImg(largeMapImage);
    // setShowPopup(true);
  };

  const { data: materials } = useGetMaterialsQuery(
    graphQLClient,
    { filters: { type: { id: { in: typeIds } } }, pagination: { pageSize: 200 } },
    {
      select: (data) => data.materials?.data as MaterialEntity[],
    },
  );

  useEffect(() => {
    if (materials?.length) {
      // // TODO - remove: temporary for testing rainbow
      // if (!materials.find((a) => a.id === '999')) {
      //   // eslint-disable-next-line
      //   // @ts-ignore
      //   materials.push(testMaterial);
      // }
      // console.log('materials', materials);
      setMaterials(materials, materialType, colourGroup);
    }
  }, [materials]);

  if (!materialGroup) {
    return null;
  }

  return (
    <>
      <div className={rootClassName}>
        {!hideColorGroups && (
          <>
            <h5>Colour</h5>
            <div className={styles.colourGroups}>
              {colourGroups.map((group) => {
                const styles: React.CSSProperties | undefined = {
                  background: group.attributes?.colour ?? '#000',
                };
                if (selectedColourGroup?.id === group.id) {
                  styles.outline = `4px solid ${group.attributes?.colour}`;
                }

                return (
                  <button key={group.id} onClick={() => setColourGroup(group)}>
                    <svg
                      style={styles}
                      xmlns='http://www.w3.org/2000/svg'
                      viewBox='0 0 42 42'
                      // strokeWidth={4}
                      // stroke={
                      //   selectedColourGroup?.id === group.id
                      //     ? '#000'
                      //     : group.attributes?.colour ?? '#000'
                      // }
                      fill={'transparent'}
                    >
                      <rect width='100%' height='100%' />
                    </svg>
                  </button>
                );
              })}
            </div>
          </>
        )}
        <MaterialType
          hideColorGroups={hideColorGroups}
          onMaterialSelected={onMaterialSelectedHandler}
          onMaterialTouchStart={handleMaterialTouchStart}
          onMaterialTouchEnd={handleMaterialTouchEnd}
          selectedMaterialId={selectedMaterialId}
        />
        <SelectedMaterialInfo />
      </div>
      {popupPortalRef &&
        showPopup &&
        createPortal(
          <div className={styles.popup} onClick={popupCloseHandler}>
            <img height='300' width='300' src={materialZoomImg} alt='' />
          </div>,
          popupPortalRef,
        )}
    </>
  );
};

const MaterialGroup = (props: MaterialGroupProps) => {
  return (
    <Provider createStore={createStore}>
      <MaterialGroupLoader {...props} />
    </Provider>
  );
};

export default MaterialGroup;
