import DontShowAgain from '@components/ui/DontShowAgain';
import { usePortalRef } from '@hooks';
import { useCustomiserStore } from '@store/customiser';
import { Falsey } from 'lodash';
import { useEffect, useState } from 'react';
import { createPortal } from 'react-dom';

interface NavHeaderScrollProps {
  styles?: {
    readonly [key: string]: string;
  };
  additionalFinishedNavs?: {
    name: string;
    isFinished: boolean;
  }[];
}

const NavHeaderScroll = ({ styles, additionalFinishedNavs }: NavHeaderScrollProps) => {
  const { navItems, selectedNav, setSelectedNav, deleteText, flags, sizing } = useCustomiserStore();
  const popupPortalRef = usePortalRef('popupContainer');
  const [showPopup, setShowPopup] = useState<boolean>(false);

  const { texts, updateText, parts } = useCustomiserStore((state) => state);
  const editText = texts?.find((g) => g.edit);

  const navIndex = navItems.findIndex((nav) => nav.name === selectedNav?.name);

  const [finishedNavs, setFinishedNavs] = useState<
    ({ name: string; isFinished: boolean } | undefined)[]
  >([]);
  console.log('flags', flags);
  useEffect(() => {
    const partsFinishedNavs = parts.map((part) => {
      const partName = part.part.name;
      if (!partName) return;
      const isFinishedPart = !!navItems.find((navItem) => {
        return navItem.name === partName;
      });
      return {
        name: partName,
        isFinished: isFinishedPart,
      };
    });
    setFinishedNavs((cur) => {
      const newFinishedNavs = [...cur, ...partsFinishedNavs, ...(additionalFinishedNavs || [])];
      if (flags.length) {
        newFinishedNavs.push({
          name: 'Flags',
          isFinished: true,
        });
      } else {
        newFinishedNavs.map((nav) => {
          if (nav?.name === 'Flags') {
            nav.isFinished = false;
          }
        });
      }
      console.log('sizing?.height?.value', sizing?.height?.value);
      console.log('sizing?.weight?.value', sizing?.weight?.value);
      if (sizing?.height?.value && sizing?.weight?.value) {
        newFinishedNavs.push({
          name: 'Size',
          isFinished: true,
        });
      } else {
        newFinishedNavs.map((nav) => {
          if (nav?.name === 'Size') {
            nav.isFinished = false;
          }
        });
      }

      const uniqueFinishedNavs = newFinishedNavs.filter(
        (nav, index, self) => self.findIndex((t) => t?.name === nav?.name) === index,
      );
      return uniqueFinishedNavs;
    });
  }, [parts, additionalFinishedNavs, flags, sizing]);

  console.log('finishedNavs', finishedNavs);
  useEffect(() => {
    if (texts.length > 0) {
      const iText = finishedNavs.findIndex((nav) => nav?.name === 'Text');
      if (iText === -1) {
        finishedNavs.push({
          name: 'Text',
          isFinished: true,
        });
      } else {
        finishedNavs.map((nav) => {
          if (nav?.name === 'Text') {
            nav.isFinished = true;
          }
        });
      }
    } else {
      finishedNavs.map((nav) => {
        if (texts.length === 0 && nav?.name === 'Text') {
          nav.isFinished = false;
        }
      });
    }
  }, [texts]);

  const isFinishedNav = (name?: string | Falsey) => {
    if (!name) return;

    const finishedNav = finishedNavs.find((nav) => nav && nav.name === name);

    if (!finishedNav) return;

    return finishedNav.isFinished;
  };

  const localStorageItem = localStorage.getItem('navConfirmText');
  const shouldShowConfirmCancelText = !localStorageItem;

  const cancelTextEdit = () => {
    if (editText?.key) {
      updateText(editText.key, {
        decalFreeze: false,
        edit: false,
        hasApplied: true,
      });

      if (!editText?.hasApplied) {
        deleteText(editText?.key);
      }
    }
    setSelectedNav(navIndex);
  };

  const navClickHandler = (index: number, name: string | Falsey) => {
    if (index === navIndex) return;
    if (editText?.text && name && name?.toLowerCase() !== 'text') {
      setShowPopup(true);
    } else {
      setSelectedNav(index);
    }
  };

  if (!popupPortalRef) {
    return null;
  }

  return (
    <>
      <div className={styles?.navHeaderScroll}>
        {navItems.map((item, index) => {
          const classes = `
            ${selectedNav?.name === item.name ? styles?.selected : ''}
            ${styles?.tab}
          `;
          return (
            <div className={classes} key={index} onClick={() => navClickHandler(index, item.name)}>
              <span className={`${styles?.dot} ${isFinishedNav(item.name) && styles?.finished}`} />
              <span className={styles?.navName}>{item.name}</span>
            </div>
          );
        })}
      </div>
      {showPopup &&
        shouldShowConfirmCancelText &&
        createPortal(
          <DontShowAgain
            cb={cancelTextEdit}
            setShowNotification={setShowPopup}
            notifName={'navConfirmText'}
          >
            <span>Cancel text customization?</span>
          </DontShowAgain>,
          popupPortalRef,
        )}
    </>
  );
};

export default NavHeaderScroll;
