import Button from '@components/ui/Button';
import { usePortalRef } from '@hooks';
import cn from 'classnames';
import ReactDOM from 'react-dom';

import styles from './NavStepButtons.module.scss';
import { TextCustomiser } from '@store/customiser';
import NavTextApplyText from '../NavText/NavTextPlaceTextGuide';

export interface NavStepButtonsProps {
  className?: string;
  step?: number;
  maxStep: number;
  onNext?: () => void;
  onPrevious?: () => void;
  onApply?: () => void;
  onPlace?: () => void;
  onCancel?: () => void;
  editText?: TextCustomiser;
}

const NavStepButtons = ({
  className,
  step,
  maxStep,
  onApply,
  onNext,
  onPrevious,
  onPlace,
  onCancel,
  editText,
}: NavStepButtonsProps) => {
  const rootClassName = cn(styles.root, className);
  const portalRef = usePortalRef('CustomiserNavActions');

  const shouldShowSave = !onPlace ? true : editText?.decalPosition ? true : false;

  if (!portalRef) {
    return null;
  }

  return ReactDOM.createPortal(
    <div className={rootClassName}>
      {step === 0 ? (
        <Button
          colour='darkGrey'
          onClick={() => {
            if (!editText?.text || (editText?.text && confirm('Cancel text customization?'))) {
              onApply && onApply();
              onCancel && onCancel();
            }
          }}
        >
          Cancel
        </Button>
      ) : (
        <Button onClick={onPrevious} disabled={step === 0}>
          Prev Step
        </Button>
      )}

      {/* <Button>View Model</Button> */}

      {step === maxStep ? (
        <>
          {shouldShowSave && (
            <Button colour='red' onClick={onApply}>
              Save
            </Button>
          )}
          {onPlace && <NavTextApplyText onClick={onPlace} styles={{ display: 'none' }} />}
        </>
      ) : (
        <Button colour='yellow' onClick={onNext}>
          Next Step
        </Button>
      )}
    </div>,
    portalRef,
  );
};

export default NavStepButtons;
