import { MaterialFragment } from '@graphql/generated/graphql';
import cn from 'classnames';
import styles from './MaterialGroup.module.scss';
import { Falsey } from 'lodash';
import { useLongPress } from './../../../hooks/useLongPress';
import { usePortalRef } from '@hooks';
import { useState } from 'react';
import { createPortal } from 'react-dom';

export interface MaterialProps {
  className?: string;
  material: MaterialFragment;
  onMaterialSelected: (material: MaterialFragment) => void;
  selectedMaterialId?: Falsey | string;
}

export const Material = ({
  className,
  material,
  onMaterialSelected,
  selectedMaterialId,
}: MaterialProps) => {
  const rootClassName = cn(styles.material, className, {
    [styles.selected]: material.id === selectedMaterialId,
  });
  const popupPortalRef = usePortalRef('popupContainer');
  const [showPopup, setShowPopup] = useState<boolean>(false);

  const mapImage = material?.attributes?.images?.find((i) => i?.mapType === 'map');

  const onClick = () => {
    console.log('mapImage', mapImage);
    if (material) {
      onMaterialSelected(material);
    }
  };

  const onLongPress = useLongPress(
    () => {
      setShowPopup(true);
      alert('a!');
    },
    onClick,
    { delay: 1000 },
  );

  return (
    <>
      <button
        className={rootClassName}
        // onTouchStart={onMaterialTouchStart}
        // onTouchEnd={onMaterialTouchEnd}
        {...onLongPress}
        data-name={material.attributes?.name}
      >
        {mapImage && <img src={mapImage?.image?.data?.attributes?.formats['thumbnail'].url} />}
      </button>
      {showPopup &&
        popupPortalRef &&
        createPortal(
          <div
            className={styles.popup}
            onClick={() => {
              setShowPopup(false);
            }}
          >
            {/* <img src={mapImage} alt="" /> */}
          </div>,
          popupPortalRef,
        )}
    </>
  );
};
