import { usePortalRef } from '@hooks';
import styles from './InfoButton.module.scss';
import { createPortal } from 'react-dom';
import { ReactNode, useState } from 'react';
import { Falsey } from 'lodash';
import InfoDisplay from './InfoDisplay';

interface Props {
  title: string | Falsey;
  propStyles?: {
    [key: string]: string;
  };
  children?: ReactNode;
}

const InfoButton = ({ title, propStyles, children }: Props) => {
  const portalRef = usePortalRef('popupContainer');
  const [shouldShow, setShouldShow] = useState<boolean>(false);

  if (!portalRef) return null;

  return (
    <>
      <div
        className={styles.infoIcon}
        onClick={(e) => {
          setShouldShow(true);
          e.stopPropagation();
        }}
      >
        <img
          src='/icons/info-icon.png'
          alt='info icon'
          className={`${styles.imgIcon} ${propStyles?.imgIcon}`}
        />
      </div>
      {shouldShow &&
        createPortal(
          <>
            <InfoDisplay
              title={title}
              cb={() => {
                setShouldShow(false);
              }}
            >
              {children}
            </InfoDisplay>
          </>,
          portalRef,
        )}
    </>
  );
};

export default InfoButton;
