import InfoDisplay from '@components/ui/InfoButton/InfoDisplay';
import { usePortalRef } from '@hooks';
import { createPortal } from 'react-dom';
import styles from './NavTextGuide.module.scss';
import { useRef, useState } from 'react';

const NavTextGuide = () => {
  const portalRef = usePortalRef('popupContainer');
  const ref = useRef<HTMLElement | null>(null);
  const [currentPage, setCurrentPage] = useState<number>(0);

  const [shouldShow, setShouldShow] = useState(true);

  const maxPageCount = 4;

  if (!portalRef) return null;

  const nextPage = () => {
    setCurrentPage((cur) => {
      if (cur < maxPageCount - 1) return cur + 1;
      return cur;
    });
  };

  const prevPage = () => {
    setCurrentPage((cur) => {
      if (cur > 0) return cur - 1;
      return cur;
    });
  };

  return createPortal(
    shouldShow && (
      <InfoDisplay
        title='HOW TO ADD TEXT'
        cb={() => {
          setShouldShow(false);
        }}
      >
        <div className={styles.body}>
          <div
            className={styles.body__display}
            ref={(el) => {
              ref.current = el;
            }}
          >
            {
              [
                <span key='1'>
                  1 - Type your text
                  <br /> *sample image*
                </span>,
                <span key='2'>
                  2 - Select Text Color
                  <br /> *sample image*
                </span>,
                <span key='3'>
                  3 - Select Outline
                  <br /> *sample image*
                </span>,
                <span key='4'>
                  4 - Select Finish
                  <br /> *sample image*
                </span>,
              ][currentPage]
            }
          </div>
          <span onClick={prevPage} className={`${styles.body__prevBtn} ${styles.body__btn}`}>
            Prev
          </span>

          {currentPage < maxPageCount - 1 ? (
            <span onClick={nextPage} className={`${styles.body__nextBtn} ${styles.body__btn}`}>
              Next
            </span>
          ) : (
            <span
              onClick={() => {
                setShouldShow(false);
              }}
              className={`${styles.body__nextBtn} ${styles.body__btn} ${styles.body__closeBtn}`}
            >
              Close
            </span>
          )}
        </div>
      </InfoDisplay>
    ),
    portalRef,
  );
};

export default NavTextGuide;
