import Button from '@components/ui/Button';
import { useCustomiserStore } from '@store/customiser';
import cn from 'classnames';

import { useDesignStore } from '@store/design';
import styles from './Header.module.scss';
import Price from '@components/ui/Price';
import { useMaterialGroupStore } from '@components/nav/MaterialGroup/MaterialGroupState';

export interface HeaderProps {
  className?: string;
}

const Header = ({ className }: HeaderProps) => {
  const rootClassName = cn(styles.root, className);
  const total = useCustomiserStore((state) => state.total());
  const designComplete = useCustomiserStore((state) => state.designComplete());
  const setResetCamera = useDesignStore((state) => state.setResetCamera);
  const addingToCart = useDesignStore((state) => state.addingToCart);

  const reset = useCustomiserStore((state) => state.reset);
  const resetNav = useCustomiserStore((state) => state.resetNav);
  const parts = useCustomiserStore((state) => state.parts);

  const setShow = useDesignStore((state) => state.setShow);
  const graphics = useCustomiserStore((state) => state.graphics);
  const selectedModels = useCustomiserStore((state) => state.selectedModels);

  const saveDesign = () => {
    setResetCamera();
  };

  const resetPopup = () => {
    console.log('graphics', graphics);
    console.log('parts', parts);
    if (confirm('Reset everything?')) {
      resetNav();
      reset();
      setShow(true);
    }
  };

  return (
    <div className={rootClassName}>
      <div id='reset-btn-container' className={styles.resetBtn}>
        <Button onClick={resetPopup}>Reset</Button>
      </div>
      <div className={styles.total}>
        Current <Price className={styles.price} priceString={total} />
      </div>
      <Button
        className={styles.addToBasket}
        onClick={saveDesign}
        disabled={!designComplete || addingToCart}
      >
        {addingToCart ? 'Saving' : 'Add to basket'}
      </Button>
    </div>
  );
};

export default Header;
