import { Decal, OrbitControls, useTexture } from '@react-three/drei';

interface NavTextPreviewSceneInterface {
  src?: string;
}

const NavTextPreviewScene = ({ src }: NavTextPreviewSceneInterface) => {
  // const texture = useTexture(src || '');
  const texture = useTexture('test-image.jpg');
  console.log('src', src);
  // const texture = useTexture('');

  return (
    <>
      <mesh>
        {/* <planeBufferGeometry args={[25, 4]} attach='geometry' /> */}
        <planeBufferGeometry args={[10, 5]} attach='geometry' />
        <meshNormalMaterial />
        <Decal
          debug
          position={[0, 0, 0]}
          rotation={[0, 0, 0]}
          scale={2}
          polygonOffset
          polygonOffsetFactor={-1}
        >
          <meshBasicMaterial map={texture} />
        </Decal>
      </mesh>
    </>
  );
};

export default NavTextPreviewScene;
