import React, { useState, useEffect } from 'react';
import styles from './DontShowAgain.module.scss';

const DontShowAgain = ({
  children,
  notifName = 'hideShowNotification',
  setShowNotification,
  cb,
}: {
  children: React.ReactNode;
  notifName: string;
  setShowNotification: React.Dispatch<React.SetStateAction<boolean>>;
  cb?: () => void;
}) => {
  const localStorageItem = localStorage.getItem(notifName);
  const [shouldShowNotification, setShouldShowNotification] = useState(!localStorageItem);
  const [isChecked, setIsChecked] = useState(false);
  console.log('localStorageItem', localStorageItem);

  const handleDismiss = () => {
    setShouldShowNotification(false);
    setShowNotification(false);
    if (isChecked) {
      localStorage.setItem(notifName, '1');
    }
  };

  return (
    <>
      {shouldShowNotification && (
        <div className={styles.root}>
          <span className={styles.notifContainer}>
            {children}
            <div className={styles.notifControlContainer}>
              <label>
                <input type='checkbox' onChange={(e) => setIsChecked(e.target.checked)} />
                Don&apos;t show again
              </label>
              <span className={styles.btnContainer}>
                <button>Cancel</button>
                <button onClick={handleDismiss}>Okay</button>
              </span>
            </div>
          </span>
        </div>
      )}
      {/* Rest of your component */}
    </>
  );
};

export default DontShowAgain;
