import { useCustomiserStore } from '@store/customiser';
import cn from 'classnames';
import { AnimatePresence, motion } from 'framer-motion';
import { Suspense, useEffect, useMemo, useRef, useState } from 'react';
import NavFitting from '../NavFitting';
import NavFlags from '../NavFlags';
import NavHeader from '../NavHeader';
import NavImages from '../NavImages';
import NavOptions from '../NavOptions';
import NavPart from '../NavPart';
import NavMinimize from '../NavMinimize';
import NavSize from '../NavSize';
import NavText from '../NavText';

import styles from './CustomiserNav.module.scss';
import { FocusCameraOnPart } from '@components/layout/Customiser/Customiser';

export interface CustomiserNavProps {
  className?: string;
  focusCameraOnPart?: FocusCameraOnPart;
}

const CustomiserNav = ({ className, focusCameraOnPart }: CustomiserNavProps) => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrollCoordinates, setScrollCoordinates] = useState({ left: 0, top: 0 });

  const {
    selectedPart,
    selectedNav,
    texts,
    isMinimizedCustomiserNav,
    updateIsMinimizedCustomiserNav,
  } = useCustomiserStore();

  const scrollableRef = useRef<HTMLDivElement | null>(null);

  const editText = useMemo(() => texts?.find((g) => g.edit), [texts]);
  const isFullscreen = useMemo(
    () => selectedNav?.name === 'Text' && editText,
    [selectedNav, editText],
  );

  const rootClassName = cn(
    styles.root,
    {
      [styles.open]: isOpen,
      [styles.minimized]: isMinimizedCustomiserNav,
      [styles.hasActions]: selectedNav?.hasActions,
      [styles.isFullscreen]: isFullscreen,
    },
    className,
  );

  const saveScrollCoordinates = () => {
    if (isOpen && scrollableRef.current) {
      setScrollCoordinates({
        left: scrollableRef.current.scrollLeft,
        top: scrollableRef.current.scrollTop,
      });
    }
  };

  useEffect(() => {
    if (isOpen && scrollableRef.current) {
      scrollableRef.current.scrollTo({
        ...scrollCoordinates,
        // Typescript issue: https://github.com/Microsoft/TypeScript/issues/28755
        // eslint-disable-next-line
        // @ts-ignore
        behavior: 'instant',
      });
    }
  }, [isOpen]);

  const toggleOpen = () => {
    saveScrollCoordinates();
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    console.log('selectedNav', selectedNav);
    console.log('isOpen', isOpen);
    setIsOpen(true);
    // if (selectedNav === null && selectedPart === null) {
    //   setIsOpen(true);
    // } else {
    //   setIsOpen(false);
    //   updateIsMinimizedCustomiserNav(false);
    // }
  }, [selectedPart, selectedNav]);

  return (
    <motion.nav
      layout='preserve-aspect'
      transition={{
        type: 'spring',
        stiffness: 700,
        damping: 40,
      }}
      className={rootClassName}
    >
      {/* {!isOpen && (
        <NavMinimize
          isMinimizedCustomiserNav={isMinimizedCustomiserNav}
          onMinimize={() => updateIsMinimizedCustomiserNav(true)}
          onMaximize={() => updateIsMinimizedCustomiserNav(false)}
        />
      )} */}
      <NavHeader
        className={styles.header}
        toggle={() => toggleOpen()}
        isOpen={isOpen}
        onClick={() => {
          updateIsMinimizedCustomiserNav(!isMinimizedCustomiserNav);
        }}
        isMinimized={isMinimizedCustomiserNav}
      />
      <div id='NavHeaderAfter' className={styles.headerAfter}></div>
      <Suspense fallback={<div>Loading...</div>}>
        <AnimatePresence initial={false}>
          <motion.div
            id='scrollable'
            className={styles.content}
            initial='collapsed'
            animate='open'
            exit='collapsed'
            variants={{
              open: { opacity: 1 },
              collapsed: { opacity: 0 },
            }}
            ref={(el) => {
              scrollableRef.current = el;
            }}
          >
            {isOpen ? (
              <NavOptions toggle={() => toggleOpen()} />
            ) : selectedNav?.type === 'fitting' ? (
              <NavFitting focusCameraOnPart={focusCameraOnPart} />
            ) : selectedNav?.type === 'names' ? (
              <NavText />
            ) : selectedNav?.type === 'flags' ? (
              <NavFlags />
            ) : selectedNav?.type === 'images' ? (
              <NavImages />
            ) : selectedNav?.type === 'size' ? (
              <NavSize />
            ) : selectedPart ? (
              <NavPart part={selectedPart} />
            ) : null}
          </motion.div>
        </AnimatePresence>
      </Suspense>
      {!isOpen && selectedNav?.hasActions && (
        <div className={styles.actions}>
          {selectedPart && <div id='CustomiserNavMaterial'></div>}
          <div id='CustomiserNavActions'></div>
        </div>
      )}
    </motion.nav>
  );
};

export default CustomiserNav;
