import { useCustomiserStore } from '@store/customiser';
import { startTransition, useEffect, useMemo, useState } from 'react';
import NavDecalAdjust from '../NavDecalAdjust';

import Accordion from '@components/ui/Accordion/Accordion';
import NavStepButtons from '../NavStepButtons';
import styles from './NavText.module.scss';
import NavTextFinish from './NavTextFinish';
import NavTextNameTypes from './NavTextNameTypes';
import NavTextOutline from './NavTextOutline';
import NavTextSelect from './NavTextSelect';
import NavTextsSelection from './NavTextsSelection';
import NavTextPreview from './NavTextPreview';
import { usePortalRef } from '@hooks';
import Button from '@components/ui/Button';
import { createPortal } from 'react-dom';

// export interface NavTextProps {

// }

const NavText = () => {
  const [step, setStep] = useState<number | undefined>(0);

  const texts = useCustomiserStore((state) => state.texts);
  const updateText = useCustomiserStore((state) => state.updateText);
  const deleteText = useCustomiserStore((state) => state.deleteText);
  const updateIsMinimizedCustomiserNav = useCustomiserStore(
    (state) => state.updateIsMinimizedCustomiserNav,
  );
  const isMinimizedCustomiserNav = useCustomiserStore((state) => state.isMinimizedCustomiserNav);
  const editText = useMemo(() => texts?.find((g) => g.edit), [texts]);
  const [showSelector, setShowSelector] = useState(editText?.key !== undefined);
  const [isPickingOutline, setIsPickingOutline] = useState(false);
  const [isPickingCrystal, setIsPickingCrystal] = useState(false);
  const navHeaderRef = usePortalRef('NavHeader');

  const noCrystalSelected = step === 3 && isPickingCrystal && !editText?.crystalBackground;
  console.log('step', step);
  console.log('isPickingCrystal', isPickingCrystal);
  console.log('!editText?.crystalBackground', !editText?.crystalBackground);
  console.log('noCrystalSelected', noCrystalSelected);
  useEffect(() => {
    if (editText?.key) {
      setShowSelector(true);
    } else {
      setShowSelector(false);
    }
  }, [editText]);

  const addText = () => {
    setShowSelector(true);
  };

  const setScale = (event: number) => {
    startTransition(() => {
      if (editText?.key) updateText(editText.key, { decalScale: Number(event) });
    });
  };

  const setRotation = (event: number) => {
    startTransition(() => {
      if (editText?.key) updateText(editText.key, { decalRotation: Number(event) });
    });
  };

  const placeText = () => {
    if (noCrystalSelected) {
      alert('Select a crystal type');
      return;
    }
    if (editText?.key && !editText?.decalPosition) {
      updateText(editText.key, {
        decalFreeze: true,
        decalPosition: [-0.01989325641440387, 2.436930108198209, 1.6226380277735046],
        decalOrientation: [
          -0.14799034762565572,
          -0.17866277612764514,
          -0.026487426629834374,
          'XYZ',
        ],
      });
    }
    updateIsMinimizedCustomiserNav(true);
  };

  const applyText = () => {
    if (editText?.key) {
      updateText(editText.key, {
        decalFreeze: false,
        edit: false,
        hasApplied: true,
      });
    }

    setStep(0);

    setShowSelector(false);
  };

  const cancelText = () => {
    if (editText?.key && !editText?.hasApplied) {
      deleteText(editText.key);
    }

    setStep(0);

    setShowSelector(false);
  };

  const handleTitleClick = (_step: number | string) => {
    if (_step === step) {
      setStep(undefined);
    } else {
      setStep(_step as number);
    }
  };

  const maxStep = 4;

  const handleNext = () => {
    if (step === undefined) {
      setStep(0);
    } else if (step < maxStep - 1) {
      switch (step) {
        case 0:
          if (!editText?.text) {
            alert('Please enter a text to continue');
            return;
          }
          break;
        case 1:
          if (!editText?.selectedName) {
            alert('Select a text color type');
            return;
          } else if (!editText?.material) {
            alert('Select a color for text');
            return;
          }
          break;
        case 2:
          if (isPickingOutline && !editText?.outline) {
            alert('Select an outline');
            return;
          }
          break;
        default:
          break;
      }

      console.log('editText', editText);
      setStep(step + 1);
    }
  };

  const handlePrevious = () => {
    if (step === undefined) {
      setStep(0);
    } else if (step > 0) {
      setStep(step - 1);
    }
  };

  return (
    <>
      {showSelector ? (
        <div className={styles.navTextEdit}>
          <NavTextPreview editText={editText} />
          <Accordion
            step={0}
            title='Text and Font'
            currentStep={step ?? -1}
            // onTitleClick={handleTitleClick}
          >
            <NavTextSelect editText={editText} />
          </Accordion>
          <Accordion
            step={1}
            title='Text Color'
            currentStep={step ?? -1}
            // onTitleClick={handleTitleClick}
          >
            <NavTextNameTypes editText={editText} />
          </Accordion>
          <Accordion
            step={2}
            title='Outline'
            currentStep={step ?? -1}
            // onTitleClick={handleTitleClick}
          >
            <NavTextOutline editText={editText} setIsPickingOutline={setIsPickingOutline} />
          </Accordion>
          <Accordion
            step={3}
            title='Finish'
            currentStep={step ?? -1}
            // onTitleClick={handleTitleClick}
          >
            <NavTextFinish
              setIsPickingCrystal={setIsPickingCrystal}
              editText={editText}
            ></NavTextFinish>
          </Accordion>
          <NavStepButtons
            step={step}
            maxStep={maxStep - 1}
            onNext={handleNext}
            onPrevious={handlePrevious}
            onApply={applyText}
            onCancel={cancelText}
            onPlace={placeText}
            editText={editText}
            noCrystalSelected={noCrystalSelected}
          />
          <NavDecalAdjust
            scale={editText?.decalScale}
            rotation={editText?.decalRotation}
            onScale={setScale}
            onRotate={setRotation}
          />
          {editText &&
            isMinimizedCustomiserNav &&
            navHeaderRef &&
            createPortal(
              <Button
                onClick={() => {
                  applyText();
                  updateIsMinimizedCustomiserNav(false);
                }}
                colour='red'
                className={styles.navHeaderSaveText}
              >
                SAVE
              </Button>,
              navHeaderRef,
            )}
        </div>
      ) : (
        <>
          <NavTextsSelection onAddTextClicked={addText}></NavTextsSelection>
        </>
      )}
    </>
  );
};

export default NavText;
