import NextStepButton from '@components/ui/Button/NextStepButton';
import PrevStepButton from '@components/ui/Button/PrevStepButton';
import { usePortalRef } from '@hooks';
import ReactDOM from 'react-dom';
import styles from './NavTextNavSteps.module.scss';
import CancelStepButton from '@components/ui/Button/CancelStepButton';
import ApplyButton from '@components/ui/Button/ApplyButton';
import InfoDisplay from '@components/ui/InfoButton/InfoDisplay';
import { useCustomiserStore } from '@store/customiser';
import { useState } from 'react';

interface NavTextNavStepsProps {
  isClickableNext?: boolean;
  isClickablePrev?: boolean;
  isVisibleNext?: boolean;
  isVisiblePrev?: boolean;

  onClickNext?: () => void;
  onClickPrev?: () => void;
  onClickCancel?: () => void;
  onClickSave?: () => void;
}

const NavTextNavSteps = ({
  isClickableNext,
  isClickablePrev,
  isVisibleNext,
  isVisiblePrev,
  onClickNext,
  onClickPrev,
  onClickCancel,
  onClickSave,
}: NavTextNavStepsProps) => {
  const portalRef = usePortalRef('CustomiserNavActions');
  const popupContainerRef = usePortalRef('popupContainer');

  const [showCancelConfirmPopup, setShowCancelConfirmPopup] = useState<boolean>(false);
  const localStorageItem = localStorage.getItem('cancelTextConfirm');
  const shouldShowConfirmCancel = !localStorageItem;

  const { texts, updateText, deleteText } = useCustomiserStore();
  const editText = texts?.find((g) => g.edit);

  const cancelTextEdit = () => {
    if (editText?.key) {
      updateText(editText.key, {
        decalFreeze: false,
        edit: false,
        hasApplied: true,
      });

      if (editText?.text && !editText?.hasApplied) {
        deleteText(editText?.key);
      }
    }
  };

  const cancelHandler = () => {
    onClickCancel && onClickCancel();
    setShowCancelConfirmPopup(true);
  };

  if (!portalRef || !popupContainerRef) {
    return null;
  }
  return (
    <>
      {/* {ReactDOM.createPortal(
        <InfoDisplay>
          <h5>Click where you want the text to go</h5>
        </InfoDisplay>,
        popupContainerRef,
      )} */}
      {showCancelConfirmPopup &&
        shouldShowConfirmCancel &&
        ReactDOM.createPortal(<div>:DDD</div>, popupContainerRef)}
      {ReactDOM.createPortal(
        <div className={styles.root}>
          {isVisiblePrev !== true && <CancelStepButton onClick={cancelHandler} />}
          {(typeof isVisiblePrev === 'undefined' || isVisiblePrev) && (
            <PrevStepButton
              isClickable={isClickablePrev}
              isVisible={isVisiblePrev}
              onClick={
                onClickPrev ||
                (() => {
                  return;
                })
              }
            />
          )}
          {(typeof isVisibleNext === 'undefined' || isVisibleNext) && (
            <NextStepButton
              isClickable={isClickableNext}
              isVisible={isVisibleNext}
              onClick={
                onClickNext ||
                (() => {
                  return;
                })
              }
            />
          )}
          {isVisibleNext !== true && (
            <ApplyButton
              onClick={
                onClickSave ||
                (() => {
                  return;
                })
              }
            />
          )}
        </div>,
        portalRef,
      )}
    </>
  );
};

export default NavTextNavSteps;
