import InfoDisplay from '@components/ui/InfoButton/InfoDisplay';
import { usePortalRef } from '@hooks';
import { createPortal } from 'react-dom';
import styles from './NavTextGuide.module.scss';
import { useEffect, useRef, useState } from 'react';

const NavTextGuide = () => {
  const portalRef = usePortalRef('popupContainer');
  const ref = useRef<HTMLElement | null>(null);
  const [currentPage, setCurrentPage] = useState<number>(0);
  let displayPagesCount = 0;

  useEffect(() => {
    console.log(displayPagesCount);
    console.log(ref.current?.childNodes.length);
    displayPagesCount = ref.current?.childNodes.length || 0;
  }, [ref]);

  if (!portalRef) return null;

  const nextPage = () => {
    setCurrentPage((cur) => {
      if (cur < displayPagesCount - 1) return cur + 1;
      return cur;
    });
  };

  return createPortal(
    <InfoDisplay title='ADDING TEXT'>
      <div className={styles.body}>
        <div
          className={styles.body__display}
          ref={(el) => {
            ref.current = el;
          }}
        >
          {
            [
              <span key='1'>1</span>,
              <span key='2'>2</span>,
              <span key='3'>3</span>,
              <span key='4'>4</span>,
            ][currentPage]
          }
        </div>
        <span className={`${styles.body__prevBtn} ${styles.body__btn}`}>Prev</span>
        <span onClick={nextPage} className={`${styles.body__nextBtn} ${styles.body__btn}`}>
          Next
        </span>
      </div>
    </InfoDisplay>,
    portalRef,
  );
};

export default NavTextGuide;
