import Button from '@components/ui/Button';
import InfoDisplay from '@components/ui/InfoButton/InfoDisplay';
import { usePortalRef } from '@hooks';
import { useRef, useState } from 'react';
import { createPortal } from 'react-dom';

interface Props {
  onClick?: () => void;
  styles?: string;
  preventShow?: boolean;
}

const NavTextApplyText = ({ onClick, styles, preventShow }: Props) => {
  const portalRef = usePortalRef('popupContainer');
  const [shouldShow, setShouldShow] = useState(false);

  const applyTextHandler = () => {
    onClick && onClick();
    if (preventShow) return;
    setShouldShow(true);
  };

  if (!portalRef) return null;

  return (
    <>
      <Button onClick={applyTextHandler} colour='red' className={styles}>
        APPLY TEXT
      </Button>
      {shouldShow &&
        createPortal(
          <InfoDisplay
            cb={() => {
              setShouldShow(false);
            }}
            title='Apply Text'
          >
            <h5>Click where you want the text to go</h5>
            <img style={{ maxWidth: '100%' }} src='examples/NavText/sample-apply-text.png' />
          </InfoDisplay>,
          portalRef,
        )}
    </>
  );
};

export default NavTextApplyText;
