/* eslint-disable react/prop-types */
import { GraphicPriceEntity, GraphicPriceFragment } from '@graphql/generated/graphql';
import cn from 'classnames';
import ReactSlider from 'react-slider';
import styles from './NavDecalAdjust.module.scss';
import { usePortalRef } from '@hooks';
import { createPortal } from 'react-dom';
import { FaCircleMinus, FaCirclePlus } from 'react-icons/fa6';
import { useState } from 'react';

import { GiClockwiseRotation, GiAnticlockwiseRotation } from 'react-icons/gi';

export interface NavDecalAdjustProps {
  className?: string;
  prices?: GraphicPriceFragment[];
  scale?: number;
  rotation?: number;
  onScale?: (event: number, price?: GraphicPriceEntity) => void;
  onRotate?: (event: number) => void;
}

const range = (start: number, stop: number, step: number) =>
  Array.from({ length: (stop - start) / step + 1 }, (value, index) => start + index * step);

const NavDecalAdjust = ({
  className,
  scale = 1,
  rotation = 0,
  prices,
  onScale,
  onRotate,
}: NavDecalAdjustProps) => {
  const scaleAdjust = 0.15;
  const rotateAdjust = 7.5;
  const portalRef = usePortalRef('CustomiserCanvasControls');

  const [currentScale, setCurrentScale] = useState(scale);
  const [currentRotate, setCurrentRotate] = useState(rotation);

  if (!portalRef) {
    return null;
  }

  const rootRotateClassName = cn(styles.root, className, styles.root__rotate);
  const rootSizeClassName = cn(styles.root, className, styles.root__scale);
  const min = 1;
  const max = 2;
  const numberOfSteps = prices?.length ?? 10;
  const step = (max - min) / (numberOfSteps - 1);
  const marks = range(min, max, step);

  const formattedPrices = prices?.map((p, i) => ({
    ...p,
    markKey: Number(marks[i].toFixed(5)),
  }));

  const onChange = (value: number) => {
    const price = formattedPrices?.find((p) => p.markKey === value);
    if (onScale) onScale(value, price);
  };

  const addScale = () => {
    setCurrentScale((cur) => {
      onScale && onScale(cur + scaleAdjust);
      return cur + scaleAdjust;
    });
  };

  const subtractScale = () => {
    setCurrentScale((cur) => {
      if (cur - scaleAdjust <= 0) return cur;
      onScale && onScale(cur - scaleAdjust);
      return cur - scaleAdjust;
    });
  };

  const rotateClockwise = () => {
    setCurrentRotate((cur) => {
      onRotate && onRotate(cur + rotateAdjust);
      return cur + rotateAdjust;
    });
  };

  const rotateCounterClockwise = () => {
    setCurrentRotate((cur) => {
      onRotate && onRotate(cur - rotateAdjust);
      return cur - rotateAdjust;
    });
  };

  return createPortal(
    <>
      <div className={rootRotateClassName}>
        <span>
          <h4>Size</h4>
          <span className={styles.iconContainer}>
            <span onClick={subtractScale}>
              <FaCircleMinus />
            </span>
            <span onClick={addScale}>
              <FaCirclePlus />
            </span>
          </span>
        </span>
        <span>
          <h4>Rotate</h4>
          <span className={styles.iconContainer}>
            <span className={styles.rotateIcon} onClick={rotateCounterClockwise}>
              <GiAnticlockwiseRotation />
            </span>
            <span className={styles.rotateIcon} onClick={rotateClockwise}>
              <GiClockwiseRotation />
            </span>
          </span>
        </span>
      </div>
    </>,
    portalRef,
  );
};

export default NavDecalAdjust;
