import { usePortalRef } from '@hooks';
import { useCustomiserStore } from '@store/customiser';
import cn from 'classnames';
import ReactDOM from 'react-dom';

import styles from './NavButtons.module.scss';
import { Falsey } from 'lodash';

import DontShowAgain from '../../ui/DontShowAgain';
import { useState } from 'react';
import ContainedButton from '@components/ui/Button/ContainedButton';
import { finished } from 'stream';

export interface NavButtonsProps {
  className?: string;
  confirmText?: string | Falsey;
  confirmStorage?: string;
}

const NavButtons = ({ className, confirmText, confirmStorage }: NavButtonsProps) => {
  const rootClassName = cn(styles.root, className);
  const cancelPartChange = useCustomiserStore((state) => state.cancelPartChange);
  const resetNav = useCustomiserStore((state) => state.resetNav);
  const navItems = useCustomiserStore((state) => state.navItems);
  const selectedNav = useCustomiserStore((state) => state.selectedNav);
  const navIndex = navItems.findIndex((nav) => nav.name === selectedNav?.name);
  const setSelectedNav = useCustomiserStore((state) => state.setSelectedNav);
  const portalRef = usePortalRef('CustomiserNavActions');

  const popupPortalRef = usePortalRef('popupContainer');
  const [showPopup, setShowPopup] = useState<boolean>(false);

  const notifTitle = confirmStorage;
  const localStorageItem = localStorage.getItem(notifTitle || '');
  const shouldShowNotification = !localStorageItem;

  const saveHandler = () => {
    if (navIndex !== -1) {
      setSelectedNav(navIndex + 1);
    }
  };

  console.log(navItems);
  const finishedNavs = [
    {
      name: 'Fitting',
      type: 'fitting',
      required: true,
      hasActions: true,
      index: 0,
    },
    {
      id: '16',
      name: 'Waist',
      type: 'part',
      required: true,
      hasActions: true,
      index: 1,
    },
    {
      id: '17',
      name: 'T shaped panel',
      type: 'part',
      required: true,
      hasActions: true,
      index: 2,
    },
    {
      id: '18',
      name: 'Second Area',
      type: 'part',
      required: true,
      hasActions: true,
      index: 3,
    },
    {
      id: '19',
      name: 'Trim',
      type: 'part',
      required: true,
      hasActions: true,
      index: 4,
    },
    {
      id: '20',
      name: 'Bottom Trim',
      type: 'part',
      required: true,
      hasActions: true,
      index: 5,
    },
    {
      id: '21',
      name: 'Crystals',
      type: 'part',
      required: false,
      hasActions: true,
      index: 6,
    },
    {
      id: '22',
      name: 'Tassels',
      type: 'part',
      required: false,
      hasActions: true,
      index: 7,
    },
    {
      name: 'Text',
      type: 'names',
      hasActions: true,
      index: 8,
    },
    {
      name: 'Flags',
      type: 'flags',
      hasActions: true,
      index: 9,
    },
    {
      name: 'Images',
      type: 'images',
      hasActions: true,
      index: 10,
    },
    {
      name: 'Size',
      type: 'size',
      required: true,
      hasActions: true,
      index: 11,
    },
  ];

  if (!portalRef || !popupPortalRef) {
    return null;
  }

  return ReactDOM.createPortal(
    <>
      <div className={rootClassName}>
        <ContainedButton onClick={() => cancelPartChange()}>Cancel</ContainedButton>
        <ContainedButton
          onClick={() => {
            // if (!confirmText) {
            //   resetNav();
            // } else {
            //   if (shouldShowNotification) {
            //     setShowPopup(true);
            //   } else {
            //     resetNav();
            //   }
            // }
            saveHandler();
          }}
        >
          Save
        </ContainedButton>
        <ContainedButton>Next</ContainedButton>
      </div>
      {confirmText &&
        showPopup &&
        shouldShowNotification &&
        notifTitle &&
        ReactDOM.createPortal(
          <DontShowAgain cb={resetNav} setShowNotification={setShowPopup} notifName={notifTitle}>
            <span>{confirmText}</span>
          </DontShowAgain>,
          popupPortalRef,
        )}
    </>,
    portalRef,
  );
};

export default NavButtons;
