import { useCustomiserStore } from '@store/customiser';
import { startTransition, useEffect, useMemo, useState } from 'react';
import NavDecalAdjust from '../NavDecalAdjust';

import Accordion from '@components/ui/Accordion/Accordion';
import NavStepButtons from '../NavStepButtons';
import styles from './NavText.module.scss';
import NavTextFinish from './NavTextFinish';
import NavTextNameTypes from './NavTextNameTypes';
import NavTextOutline from './NavTextOutline';
import NavTextSelect from './NavTextSelect';
import NavTextsSelection from './NavTextsSelection';
import NavTextPreview from './NavTextPreview';

// export interface NavTextProps {

// }

const NavText = () => {
  const [step, setStep] = useState<number | undefined>(0);

  const texts = useCustomiserStore((state) => state.texts);
  const updateText = useCustomiserStore((state) => state.updateText);
  const deleteText = useCustomiserStore((state) => state.deleteText);
  const updateIsMinimizedCustomiserNav = useCustomiserStore(
    (state) => state.updateIsMinimizedCustomiserNav,
  );
  const editText = useMemo(() => texts?.find((g) => g.edit), [texts]);
  const [showSelector, setShowSelector] = useState(editText?.key !== undefined);

  useEffect(() => {
    if (editText?.key) {
      setShowSelector(true);
    } else {
      setShowSelector(false);
    }
  }, [editText]);

  const addText = () => {
    setShowSelector(true);
  };

  const setScale = (event: number) => {
    startTransition(() => {
      if (editText?.key) updateText(editText.key, { decalScale: Number(event) });
    });
  };

  const setRotation = (event: number) => {
    startTransition(() => {
      if (editText?.key) updateText(editText.key, { decalRotation: Number(event) });
    });
  };

  const placeText = () => {
    if (editText?.key && !editText?.decalPosition) {
      updateText(editText.key, {
        decalFreeze: true,
        decalPosition: [-0.01989325641440387, 2.436930108198209, 1.6226380277735046],
        decalOrientation: [
          -0.14799034762565572,
          -0.17866277612764514,
          -0.026487426629834374,
          'XYZ',
        ],
      });
    }
    updateIsMinimizedCustomiserNav(true);
  };

  const applyText = () => {
    if (editText?.key) {
      updateText(editText.key, {
        decalFreeze: false,
        edit: false,
        hasApplied: true,
      });
    }

    setStep(0);

    setShowSelector(false);
  };

  const cancelText = () => {
    if (editText?.key && !editText?.hasApplied) {
      deleteText(editText.key);
    }

    setStep(0);

    setShowSelector(false);
  };

  const handleTitleClick = (_step: number | string) => {
    if (_step === step) {
      setStep(undefined);
    } else {
      setStep(_step as number);
    }
  };

  const maxStep = 4;

  const handleNext = () => {
    if (step === undefined) {
      setStep(0);
    } else if (step < maxStep - 1) {
      if (step === 0 && editText?.text === '') {
        alert('Please enter a text to continue');
        return;
      }
      // else if (step === 2 && )
      setStep(step + 1);
    }
  };

  const handlePrevious = () => {
    if (step === undefined) {
      setStep(0);
    } else if (step > 0) {
      setStep(step - 1);
    }
  };

  return (
    <>
      {showSelector ? (
        <div className={styles.navTextEdit}>
          <NavTextPreview editText={editText} />
          <Accordion
            step={0}
            title='Text and Font'
            currentStep={step ?? -1}
            // onTitleClick={handleTitleClick}
          >
            <NavTextSelect editText={editText} />
          </Accordion>
          <Accordion
            step={1}
            title='Text Color'
            currentStep={step ?? -1}
            // onTitleClick={handleTitleClick}
          >
            <NavTextNameTypes editText={editText} />
          </Accordion>
          <Accordion
            step={2}
            title='Outline'
            currentStep={step ?? -1}
            // onTitleClick={handleTitleClick}
          >
            <NavTextOutline editText={editText} />
          </Accordion>
          <Accordion
            step={3}
            title='Finish'
            currentStep={step ?? -1}
            // onTitleClick={handleTitleClick}
          >
            <NavTextFinish editText={editText}></NavTextFinish>
          </Accordion>

          <NavStepButtons
            step={step}
            maxStep={maxStep - 1}
            onNext={handleNext}
            onPrevious={handlePrevious}
            onApply={applyText}
            onCancel={cancelText}
            onPlace={placeText}
            editText={editText}
          />

          <NavDecalAdjust
            scale={editText?.decalScale}
            rotation={editText?.decalRotation}
            onScale={setScale}
            onRotate={setRotation}
          />
        </div>
      ) : (
        <>
          <NavTextsSelection onAddTextClicked={addText}></NavTextsSelection>
        </>
      )}
    </>
  );
};

export default NavText;
