import Button from '@components/ui/Button';
import { usePortalRef } from '@hooks';
import { useCustomiserStore } from '@store/customiser';
import cn from 'classnames';
import ReactDOM from 'react-dom';

import styles from './NavButtons.module.scss';
import { Falsey } from 'lodash';

import DontShowAgain from '../../ui/DontShowAgain';
import { useState } from 'react';
import ContainedButton from '@components/ui/Button/ContainedButton';

export interface NavButtonsProps {
  className?: string;
  confirmText?: string | Falsey;
  confirmStorage?: string;
}

const NavButtons = ({ className, confirmText, confirmStorage }: NavButtonsProps) => {
  const rootClassName = cn(styles.root, className);
  const cancelPartChange = useCustomiserStore((state) => state.cancelPartChange);
  const resetNav = useCustomiserStore((state) => state.resetNav);
  const savedParts = useCustomiserStore((state) => state.savedParts);
  const portalRef = usePortalRef('CustomiserNavActions');

  const popupPortalRef = usePortalRef('popupContainer');
  const [showPopup, setShowPopup] = useState<boolean>(false);

  const notifTitle = confirmStorage;
  const localStorageItem = localStorage.getItem(notifTitle || '');
  const shouldShowNotification = !localStorageItem;

  if (!portalRef || !popupPortalRef) {
    return null;
  }

  return ReactDOM.createPortal(
    <>
      <div className={rootClassName}>
        <ContainedButton onClick={() => cancelPartChange()}>Cancel</ContainedButton>
        <ContainedButton
          onClick={() => {
            if (!confirmText) {
              resetNav();
            } else {
              if (shouldShowNotification) {
                setShowPopup(true);
              } else {
                resetNav();
              }
            }
          }}
        >
          Save
        </ContainedButton>
      </div>
      {confirmText &&
        showPopup &&
        shouldShowNotification &&
        notifTitle &&
        ReactDOM.createPortal(
          <DontShowAgain cb={resetNav} setShowNotification={setShowPopup} notifName={notifTitle}>
            <span>{confirmText}</span>
          </DontShowAgain>,
          popupPortalRef,
        )}
    </>,
    portalRef,
  );
};

export default NavButtons;
