// @ts-nocheck
import Button from '@components/ui/Button';
import { usePortalRef } from '@hooks';
import cn from 'classnames';
import ReactDOM from 'react-dom';

import styles from './NavStepButtons.module.scss';
import { TextCustomiser } from '@store/customiser';
import NavTextApplyText from '../NavText/NavTextPlaceTextGuide';

export interface NavStepButtonsProps {
  className?: string;
  step?: number;
  maxStep: number;
  onNext?: () => void;
  onPrevious?: () => void;
  onApply?: () => void;
  onPlace?: () => void;
  editText?: TextCustomiser;
}

const NavStepButtons = ({
  className,
  step,
  maxStep,
  onApply,
  onNext,
  onPrevious,
  onPlace,
  editText,
}: NavStepButtonsProps) => {
  const rootClassName = cn(styles.root, className);
  const portalRef = usePortalRef('CustomiserNavActions');

  if (!portalRef) {
    return null;
  }

  return ReactDOM.createPortal(
    <div className={rootClassName}>
      {step === 0 ? (
        <Button colour='darkGrey' onClick={onApply}>
          Cancel
        </Button>
      ) : (
        <Button onClick={onPrevious} disabled={step === 0}>
          Prev Step
        </Button>
      )}

      {/* <Button>View Model</Button> */}

      {step === maxStep ? (
        (!editText?.decalPosition && onPlace) || true ? (
          <NavTextApplyText onClick={onPlace} />
        ) : (
          <Button colour='red' onClick={onApply}>
            Save
          </Button>
        )
      ) : (
        <Button colour='yellow' onClick={onNext}>
          Next Step
        </Button>
      )}
    </div>,
    portalRef,
  );
};

export default NavStepButtons;
