import InfoDisplay from '@components/ui/InfoButton/InfoDisplay';
import { usePortalRef } from '@hooks';
import { createPortal } from 'react-dom';
import styles from './NavTextGuide.module.scss';
import { useRef, useState } from 'react';

const NavTextGuide = () => {
  const portalRef = usePortalRef('popupContainer');
  const ref = useRef<HTMLElement | null>(null);
  const [currentPage, setCurrentPage] = useState<number>(0);

  const nextPage = () => {
    setCurrentPage((cur) => {
      return cur++;
    });
  };

  if (!portalRef) return null;
  return createPortal(
    <InfoDisplay title='ADDING TEXT'>
      <div className={styles.body}>
        <div
          className={styles.body__display}
          ref={(el) => {
            ref.current = el;
          }}
        >
          [<span>1</span>
          <span>2</span>
          <span>3</span>
          <span>4</span>]
        </div>
        <span className={`${styles.body__prevBtn} ${styles.body__btn}`}>Prev</span>
        <span className={`${styles.body__nextBtn} ${styles.body__btn}`}>Next</span>
      </div>
    </InfoDisplay>,
    portalRef,
  );
};

export default NavTextGuide;
