import { Decal, OrbitControls, useTexture } from '@react-three/drei';
import { Texture, Vector3 } from 'three';

interface NavTextPreviewSceneInterface {
  src: string;
  text?: any;
}

const NavTextPreviewScene = ({ src, text }: NavTextPreviewSceneInterface) => {
  const texture = useTexture(src);
  // const texture = useTexture('test-image.jpg');
  console.log('src', src);
  // const texture = useTexture('');

  const scale = 10;
  const scaleModifier = new Vector3(3.2 * scale, 0.4 * scale, 4);

  return (
    <>
      <mesh>
        {/* <planeBufferGeometry args={[25, 4]} attach='geometry' /> */}
        <planeBufferGeometry args={[20, 5]} />
        <meshNormalMaterial />
        <Decal position={[0, 0, 0]} rotation={[0, 0, 0]} scale={scaleModifier}>
          <meshBasicMaterial
            map={text.preview instanceof Texture ? text.preview : null}
            transparent
          />
        </Decal>
      </mesh>
    </>
  );
};

export default NavTextPreviewScene;
