import React, { useState, useEffect } from 'react';
import styles from './DontShowAgain.module.scss';

const DontShowAgain = ({
  children,
  notifName = 'hideShowNotification',
  setShowNotification,
  showNotification,
}: {
  children: React.ReactNode;
  notifName: string;
  setShowNotification: React.Dispatch<React.SetStateAction<boolean>>;
  showNotification: boolean;
}) => {
  const [shouldShowNotification, setShouldShowNotification] = useState(false);
  const [isChecked, setIsChecked] = useState(false);

  useEffect(() => {
    const shouldHide = localStorage.getItem(notifName);
    console.log('shouldHide', shouldHide);
    if (!shouldHide) {
      setShouldShowNotification(true);
      setShowNotification(true);
    }
  }, [showNotification]);

  const handleDismiss = () => {
    setShouldShowNotification(false);
    setShowNotification(false);
    if (isChecked) {
      localStorage.setItem(notifName, '1');
    }
  };

  return (
    <>
      {shouldShowNotification && (
        <div className={styles.root}>
          <span className={styles.notifContainer}>
            {children}
            <label>
              <input
                type='checkbox'
                onChange={(e) => setIsChecked(e.target.checked)}
                // checked={() => {alert(':DDD')}}
                // onChange={handleCheckboxChange}
              />
              Don&apos;t show again
            </label>
            <button onClick={handleDismiss}>Okay</button>
          </span>
        </div>
      )}
      {/* Rest of your component */}
    </>
  );
};

export default DontShowAgain;
