import DontShowAgain from '@components/ui/DontShowAgain';
import { usePortalRef } from '@hooks';
import { useCustomiserStore } from '@store/customiser';
import { Falsey } from 'lodash';
import { useState } from 'react';
import { createPortal } from 'react-dom';

interface NavHeaderScrollProps {
  styles?: {
    readonly [key: string]: string;
  };
}

const NavHeaderScroll = ({ styles }: NavHeaderScrollProps) => {
  const { navItems, selectedNav, setSelectedNav } = useCustomiserStore();
  const popupPortalRef = usePortalRef('popupContainer');
  const [showPopup, setShowPopup] = useState<boolean>(false);

  const { texts } = useCustomiserStore((state) => state);
  const editText = texts?.find((g) => g.edit);

  const [navIndex, setNavIndex] = useState<number>(0);

  const localStorageItem = localStorage.getItem('navConfirmText');
  const shouldShowNotification = !localStorageItem;

  const cancelTextEdit = () => {
    setSelectedNav(navIndex);
  };

  const navClickHandler = (index: number, name: string | Falsey) => {
    setNavIndex(index);
    if (editText?.text && name && name?.toLowerCase() !== 'text') {
      setShowPopup(true);
    } else {
      setSelectedNav(index);
    }
  };

  if (!popupPortalRef) {
    return null;
  }

  return (
    <>
      <div className={styles?.navHeaderScroll}>
        {navItems.map((item, index) => {
          const classes = selectedNav?.name === item.name ? styles?.selected : '';
          return (
            <div className={classes} key={index} onClick={() => navClickHandler(index, item.name)}>
              {item.name}
            </div>
          );
        })}
      </div>
      {showPopup &&
        shouldShowNotification &&
        createPortal(
          <DontShowAgain
            cb={cancelTextEdit}
            setShowNotification={setShowPopup}
            notifName={'navConfirmText'}
          >
            <span>Cancel text customization?</span>
          </DontShowAgain>,
          popupPortalRef,
        )}
    </>
  );
};

export default NavHeaderScroll;
